sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

isDatePast: function (strDate) {
	var rightnow = new GlideDateTime();
	var testDate = new GlideDateTime(strDate);

	if (testDate.before(rightnow)) {
		return true;
	}
	else {
		return false;
	}		
},

isDateToday: function (strDate) {
	var todayDate = new GlideDateTime();
	var datePassed = new GlideDateTime(strDate);

	var onlyDate = todayDate.getLocalDate();
	var datePassedOnlyDate = datePassed.getLocalDate();

	if (onlyDate.compareTo(datePassedOnlyDate) == 0) {
		return true;
	}
	else {
		return false;
	}
},
    type: 'NeedItUtils'
};
